INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52024, 3076, 'Fludrocortisone', 'Water-Electrolyte Imbalance', 'Corticosteroids can cause hypernatremia, hypokalemia, and fluid retention.  These mineralocorticoid effects are most significant with fludrocortisone, followed by hydrocortisone and cortisone, then by prednisone and prednisolone.  The remaining corticosteroids, betamethasone, dexamethasone, methylprednisolone, and triamcinolone, have little mineralocorticoid activities.  However, large doses of any corticosteroid can demonstrate these effects, particularly if given for longer than brief periods.  All corticosteroids also increase excretion of calcium and can cause hypocalcemia.  Therapy with corticosteroids should be administered cautiously in patients with preexisting electrolyte disturbances.  Caution is also advised when treating patients with seizure disorders, since electrolyte disturbances may trigger seizure activity.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52025, 14506, 'Fludrocortisone', 'Water-Electrolyte Imbalance', 'Corticosteroids can cause hypernatremia, hypokalemia, and fluid retention.  These mineralocorticoid effects are most significant with fludrocortisone, followed by hydrocortisone and cortisone, then by prednisone and prednisolone.  The remaining corticosteroids, betamethasone, dexamethasone, methylprednisolone, and triamcinolone, have little mineralocorticoid activities.  However, large doses of any corticosteroid can demonstrate these effects, particularly if given for longer than brief periods.  All corticosteroids also increase excretion of calcium and can cause hypocalcemia.  Therapy with corticosteroids should be administered cautiously in patients with preexisting electrolyte disturbances.  Caution is also advised when treating patients with seizure disorders, since electrolyte disturbances may trigger seizure activity.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52026, 3076, 'Fludrocortisone', 'Peptic Ulcer Perforation', 'Corticosteroids may cause gastrointestinal perforation and hemorrhage, usually when given in high dosages or for prolonged periods.  They may also mask symptoms of complications such as peritonitis or intraabdominal sepsis.  Therapy with corticosteroids should be avoided or administered cautiously in patients with diverticulitis, nonspecific ulcerative colitis (if there is a probability of impending perforation, abscess, or other pyogenic infection), or recent intestinal anastomoses.', '2', '', 'DDInter', 0);
