INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52036, 3076, 'Fludrocortisone', 'Myocardial Infarction', 'The use of corticosteroids may be associated with left ventricular free-wall rupture in patients who have had a recent myocardial infarction.  Pharmacologic dosages of corticosteroids should be administered with great caution in such patients.', '2', '', 'DDInter', 0);
