INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52083, 3, 'Fluorouracil', 'Dihydropyrimidine Dehydrogenase Deficiency', 'Fluorouracil is metabolized to an inactive form by dihydropyrimidine dehydrogenase.  Inherited deficiency of this enzyme results in decreased clearance of 5-FU and severe toxicity such as stomatitis, diarrhea, neutropenia, and neurotoxicity.  Rechallenge with dosage reduction resulted in recurrence and progression of toxicity.  Therapy with fluorouracil should not be administered to patients with dihydropyrimidine deficiency.', '3', '', 'DDInter', 0);
