INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52089, 3, 'Fluorouracil', 'Infections', 'Fluorouracil (5-FU) should not be used in patients with potentially serious infectious diseases.  5-FU induces myelosuppression.  All patients should be instructed to immediately report any signs or symptoms suggesting infection such as fever, sore throat, or local infection during therapy with 5-FU.  Close clinical monitoring of hematopoietic function is recommended.', '3', '', 'DDInter', 0);
