INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52301, 17971, 'Fluoxetine', 'Kidney Diseases', 'Fluoxetine is primarily metabolized by the liver.  All but one metabolites are inactive, and they are excreted by the kidney.  The clearance of norfluoxetine, the active metabolite, is not dependent on renal function.  Dosage adjustments are generally not deemed necessary in patients with impaired renal function, although the clinical significance of possible metabolite accumulation is unknown.  Caution may be warranted when fluoxetine therapy is administered in patients with severe renal dysfunction.', '1', '', 'DDInter', 0);
