INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52385, 2064, 'Amiodarone', 'Vision Disorders', 'Optic neuropathy and/or neuritis has occurred during administration of amiodarone and has resulted in visual impairment such as changes in visual acuity, decrease in peripheral vision, and blindness.  Optic toxicity can occur at any time following initiation of amiodarone.  Therapy with amiodarone should be administered cautiously in patients with visual defects.  Regular ophthalmologic examinations including fundoscopy and slit- lamp examinations are recommended.', '3', '', 'DDInter', 0);
