INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52401, 2064, 'Amiodarone', 'Cardiomyopathies', 'Antiarrhythmic agents can induce severe hypotension (particularly with IV administration) or induce or worsen congestive heart failure (CHF).  Patients with primary cardiomyopathy or inadequately compensated CHF are at increased risk.  Antiarrhythmic agents should be administered cautiously and dosage and/or frequency of administration modified in patients with hypotension or adequately compensated CHF.  Alternative therapy should be considered unless these conditions are secondary to cardiac arrhythmia.', '3', '', 'DDInter', 0);
