INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52433, 2064, 'Amiodarone', 'Hepatic Insufficiency', 'There have been rare reports of fatal hepatocellular necrosis after treatment with amiodarone.  In patients with life-threatening arrhythmias, the potential risk of hepatic injury should be weighed against the potential benefit of amiodarone therapy.  Patients with hepatic impairment should be monitored for evidence of progressive hepatic injury.  Consideration should be given to reducing the rate of administration or withdrawing treatment if needed.', '2', '', 'DDInter', 0);
