INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52465, 2064, 'Amiodarone', 'Hypothyroidism', 'Amiodarone inhibits peripheral conversion of thyroxine (T4) to triiodothyronine (T3) and also contributes inorganic iodine that can result in altered thyroid function tests, hypothyroidism, or hyperthyroidism.  Therapy with amiodarone should be administered cautiously in patients with thyroid dysfunction.  Clinical monitoring, including baseline and periodic evaluation of thyroid function is recommended.   Modification of thyroid therapy may be necessary.', '2', '', 'DDInter', 0);
