INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52528, 8269, 'Fluphenazine', 'Respiratory Insufficiency', 'The use of phenothiazines is contraindicated in comatose patients and patients with severe central nervous system depression.  Phenothiazines may potentiate the CNS and respiratory depression in these patients.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52529, 8270, 'Fluphenazine', 'Respiratory Insufficiency', 'The use of phenothiazines is contraindicated in comatose patients and patients with severe central nervous system depression.  Phenothiazines may potentiate the CNS and respiratory depression in these patients.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52530, 8269, 'Fluphenazine', 'Craniocerebral Trauma', 'The use of phenothiazines is contraindicated in patients with suspected or established subcortical brain damage, with or without hypothalamic involvement.  Phenothiazines can interfere with thermoregulatory mechanisms, and a hyperthermic reaction with temperatures in excess of 104 F may occur in such patients, sometimes not until 14 to 16 hours after drug administration.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52531, 8270, 'Fluphenazine', 'Craniocerebral Trauma', 'The use of phenothiazines is contraindicated in patients with suspected or established subcortical brain damage, with or without hypothalamic involvement.  Phenothiazines can interfere with thermoregulatory mechanisms, and a hyperthermic reaction with temperatures in excess of 104 F may occur in such patients, sometimes not until 14 to 16 hours after drug administration.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52532, 8269, 'Fluphenazine', 'Anticholinergic Syndrome', 'Phenothiazines have anticholinergic activity, to which elderly patients are particularly sensitive.  Low-potency agents such as chlorpromazine and thioridazine tend to exhibit greater anticholinergic effects than other agents in the class.  Therapy with phenothiazines should be administered cautiously in patients with preexisting conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention or obstruction; angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma; and gastrointestinal obstructive disorders.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52533, 8270, 'Fluphenazine', 'Anticholinergic Syndrome', 'Phenothiazines have anticholinergic activity, to which elderly patients are particularly sensitive.  Low-potency agents such as chlorpromazine and thioridazine tend to exhibit greater anticholinergic effects than other agents in the class.  Therapy with phenothiazines should be administered cautiously in patients with preexisting conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention or obstruction; angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma; and gastrointestinal obstructive disorders.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52534, 8269, 'Fluphenazine', 'Breast Neoplasms', 'The chronic use of phenothiazines is associated with persistent elevations in prolactin levels.  Based on in vitro data, approximately one-third of human breast cancers are thought to be prolactin-dependent.  The clinical significance of this observation is unknown.  Chronic administration of neuroleptic drugs has been associated with mammary tumorigenesis in rodent studies but not in human clinical or epidemiologic studies.  Therapy with phenothiazines should be administered cautiously in patients with existing or suspected malignancy of the breast.', '2', '', 'DDInter', 0);
