INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52546, 8269, 'Fluphenazine', 'Kidney Diseases', 'Phenothiazines and their metabolites are excreted by the kidney.  There are very limited data concerning the use of phenothiazines in patients with renal disease.  Therapy with phenothiazines should be administered cautiously in patients with significantly impaired renal function.  The manufacturers recommend periodic renal function tests for all patients during prolonged therapy.', '2', '', 'DDInter', 0);
