INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52597, 18227, 'Fosaprepitant', 'Long QT Syndrome', 'Fosaprepitant, a prodrug of aprepitant, is a weak inhibitor of CYP3A4, and aprepitant is a substrate, inhibitor, and inducer of CYP3A4.  An increased plasma concentration of fosaprepitant could cause QT prolongation.  Caution is advised in patients with long QT, and in patients receiving other drugs that are CYP3A4 substrates as it may result in a risk of higher plasma concentrations and adverse events.  Close monitoring is recommended in these patients.', '2', '', 'DDInter', 0);
