INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52640, 5060, 'Folic acid', 'Anemia', 'The use of folic acid is contraindicated in patients with undiagnosed anemia.  Folic acid in dosages above 1 mg/day can obscure the diagnosis of pernicious anemia by alleviating the hematologic abnormalities while allowing the progression of neurologic complications.  In addition, folic acid alone is improper therapy in the treatment of pernicious anemia and other megaloblastic anemias where vitamin B12 is deficient.', '3', '', 'DDInter', 0);
