INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52718, 14149, 'Flutamide', 'Hemolysis', 'Toxicity consistent with aniline exposure such as methemoglobinemia, hemolytic anemia, and cholestatic jaundice has been reported during flutamide therapy.  Therapy with flutamide should be administered cautiously in patients with glucose-6-phosphate deficiency, hemoglobin M disease, and patients who smoke.  Clinical monitoring of methemoglobin levels in susceptible individuals is recommended,', '2', '', 'DDInter', 0);
