INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52762, 4738, 'Fluvoxamine', 'Weight Loss', 'The use of selective serotonin reuptake inhibitors (SSRIs) may occasionally cause significant weight loss, which may be undesirable in patients suffering from anorexia, malnutrition or excessive weight loss.  Anorexia may occur in approximately 5% to 10% of patients.  Weight change should be monitored during therapy if an SSRI is used in these patients.', '1', '', 'DDInter', 0);
