INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52814, 591, 'Amisulpride', 'Long QT Syndrome', 'Amisulpride causes dose and concentration-dependent prolongation of the QT interval and should be avoided in patients with congenital long QT syndrome and in patients taking some drugs that prolong the QT interval such as droperidol.  ECG monitoring is recommended in patients with pre-existing arrhythmias and cardiac conduction disorders; electrolyte abnormalities (e.g., hypokalemia or hypomagnesemia); congestive heart failure; and in patients taking other medicinal products or with other medical conditions known to prolong the QT interval.', '2', '', 'DDInter', 0);
