INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52842, 19365, 'Fosfomycin', 'Kidney Diseases', 'Fosfomycin is eliminated unchanged by the kidney (38%) and in the feces (18%).  The excretion of fosfomycin is significantly decreased and the half-life prolonged in patients with compromised renal function.  Side effects (e.g., diarrhea, headache, nausea, vaginitis) may be prolonged in patients with renal impairment.', '1', '', 'DDInter', 0);
