INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52884, 11749, 'Amitriptyline', 'Epilepsy', 'Tricyclic antidepressants (TCAs), can lower the seizure threshold and trigger seizures.  These drugs should be used with extreme caution in patients with a history of seizures, or other predisposing factors, such as head trauma, CNS abnormalities, and alcoholism.  Daily dose restrictions might apply for specific antidepressants.  Physicians are encouraged to get additional dosing recommendations on the manufacturer''s prescribing information.', '3', '', 'DDInter', 0);
