INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52908, 11749, 'Amitriptyline', 'Liver Diseases', 'Tricyclic and tetracyclic antidepressants (TCAs) are known to undergo metabolism in the liver.  Some of the metabolites, such as those of imipramine, clomipramine and desipramine, may be pharmacologically active.  Many of the metabolites are also excreted by the kidney.  There are very limited data concerning the use of TCAs in patients with renal and/or liver disease.  Therapy with TCAs should be administered cautiously in patients with significantly impaired renal or hepatic function.  Dosage adjustments may be necessary.', '2', '', 'DDInter', 0);
