INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52916, 11749, 'Amitriptyline', 'Schizophrenia', 'Tricyclic antidepressants (TCAs) may aggravate symptoms of psychosis in schizophrenic patients, particularly those with paranoid symptomatology.  Depressed patients, usually those with bipolar disorder, may experience a switch from depression to mania or hypomania.  These occurrences have also been reported rarely with the tetracyclic antidepressant, maprotiline.  Therapy with these agents should be administered cautiously in patients with schizophrenia, bipolar disorder, or a history of mania.', '2', '', 'DDInter', 0);
