INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52924, 11749, 'Amitriptyline', 'Tardive Dyskinesia', 'Tricyclic and tetracyclic antidepressants (TCAs) have anticholinergic activity, to which elderly patients are particularly sensitive.  Tertiary amines such as amitriptyline and trimipramine tend to exhibit greater anticholinergic effects than other agents in the class.  As with other drugs that possess anticholinergic activity, TCAs may aggravate tardive dyskinesia or induce previously suppressed symptoms.  Patients with tardive dyskinesia requiring therapy with TCAs should be monitored for exacerbation of the condition.', '2', '', 'DDInter', 0);
