INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52940, 11749, 'Amitriptyline', 'Bipolar Disorder', 'A major depressive episode can be the initial presentation of bipolar disorder.  Patients with depressive symptoms should be adequately screened to determine if they are at risk for bipolar disorder prior to initiating treatment with a tricyclic antidepressant.  This screening should include a detailed psychiatric history, including a family history of suicide, bipolar disorder, and depression.  It should be noted that tricyclic antidepressants are not approved for use in treating bipolar depression.', '2', '', 'DDInter', 0);
