INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52986, 8300, 'Fosinopril', 'Diseases requiring hemodialysis', 'Anaphylactoid reactions have been reported in patients undergoing hemodialysis with high-flux polyacrylonitrile membranes and treated concomitantly with an ACE inhibitor.  The frequency and mechanism of this interaction have not been established, and it is not known whether the interaction occurs with other membrane types.  Therapy with ACE inhibitors should be administered cautiously in patients requiring hemodialysis.', '3', '', 'DDInter', 0);
