INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52998, 0, 'Fostemsavir', 'Hepatitis B', 'Fostemsavir may cause elevations in hepatic transaminases in patients co-infected with hepatitis B or C.  It is recommended to monitor liver function in patients with hepatitis B or C co-infection.  Particular attention should be practiced in initiating or maintaining effective hepatitis B therapy when starting fostemsavir treatment in patients co-infected with hepatitis B.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52999, 0, 'Fostemsavir', 'Long QT Syndrome', 'Fostemsavir has been shown to significantly prolong the QTc interval of the electrocardiogram with higher than recommended dosages.  Fostemsavir should be used with caution in patients with a history of QTc interval prolongation, when co-administered with a drug with a known risk of Torsade de Pointes, or in patients with relevant preexisting cardiac disease.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53000, 0, 'Fostamatinib', 'Diarrhea', 'The use of fostamatinib may cause diarrhea.  Monitor patients for the development of diarrhea during treatment with fostamatinib.  It is recommended to manage diarrhea using supportive care measures, including dietary changes, hydration and/or antidiarrheal medication, early after the onset of symptoms.  Interrupt, dose reduce, or discontinue treatment if diarrhea becomes severe (Grade 3 or above).  Care should be exercised when using this agent in patients with diarrhea.', '2', '', 'DDInter', 0);
