INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53010, 6618, 'Fulvestrant', 'Hemorrhage', 'Fulvestrant injection is administered intramuscularly and site related events have been reported.  It is recommended to use caution in patients with bleeding diatheses, thrombocytopenia, or anticoagulant use.  In addition, caution should be taken while administering fulvestrant injection at the dorsogluteal injection site due to the proximity of the underlying sciatic nerve.', '2', '', 'DDInter', 0);
