INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53047, 3765, 'Furosemide', 'Fibrosis', 'Loop diuretic therapy should be initiated in the hospital under strict observation in patients with liver cirrhosis and ascites.  Sudden alteration of fluid and electrolyte balance may precipitate hepatic encephalopathy and coma in such patients, who are also at increased risk for the development of hypokalemia.  Supplemental potassium and/or concomitant use of an aldosterone antagonist or potassium-sparing agent may help prevent hypokalemia and metabolic alkalosis.  Loop diuretics should be withheld in patients with hepatic coma until the condition improves.', '3', '', 'DDInter', 0);
