INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53201, 23969, 'Furosemide', 'Diabetes Mellitus', 'Loop diuretics may cause hyperglycemia, glycosuria, and alterations in glucose tolerance tests.  Rarely, precipitation of diabetes mellitus has been reported.  Therapy with loop diuretics should be administered cautiously in patients with diabetes mellitus, glucose intolerance, or a predisposition to hyperglycemia.  Patients with diabetes mellitus should be monitored more closely during therapy, and their antidiabetic regimen adjusted accordingly.', '2', '', 'DDInter', 0);
