INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53782, 6611, 'Gefitinib', 'Eye Diseases', 'Some epidermal growth factor receptor (EGFR) inhibitors should be administered cautiously in patients with ocular disorders such as primary keratitis, ulcerative keratitis, or conjunctivitis.  It is recommended to interrupt or discontinue treatment with these agents if patients present with acute or worsening ocular disorders.', '3', '', 'DDInter', 0);
