INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53784, 6611, 'Gefitinib', 'Lung Diseases', 'The use of certain EGFR inhibitors has been associated with pulmonary toxicity.  Serious cases of interstitial lung disease (ILD), including fatal cases and interstitial pneumonitis or pulmonary fibrosis have been reported.  Caution is recommended when using these agents in patients with a history of interstitial pneumonitis or pulmonary fibrosis or those patients presenting with acute onset of new or progressive unexplained pulmonary symptoms such as dyspnea, cough, and fever pending diagnostic evaluation.  If ILD is confirmed, permanently discontinue these agents and institute appropriate measures.', '3', '', 'DDInter', 0);
