INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53788, 6611, 'Gefitinib', 'Kidney Diseases', 'Less than four percent (<4%) of gefitinib and its metabolites are excreted via the kidney.  Population pharmacokinetic analyses suggest that creatinine clearance (above 20 mL/min) has no clinically meaningful effect on predicted steady state trough concentration of gefitinib.  No clinical studies were conducted with gefitinib in patients with severe renal impairment.', '2', '', 'DDInter', 0);
