INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53798, 7516, 'Gemfibrozil', 'Hypolipoproteinemias', 'There have been reports of severe decreases in HDL cholesterol (HDL-C) levels (as low as 2 mg/dL) occurring in diabetic and non-diabetic patients initiated on fibric acid derivatives therapy.  The decrease has been reported to occur within 2 weeks to years after initiation of therapy.  It is recommended that HDL-C levels be checked within the first few months after initiation therapy and if a severely depressed HDL-C level is detected, therapy with these agents should be withdrawn.  Monitor HDL-C level until it has returned to baseline, and therapy with these agents should not be re-initiated in these patients.', '3', '', 'DDInter', 0);
