INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53806, 7516, 'Gemfibrozil', 'Liver Diseases', 'The use of gemfibrozil is considered by the manufacturer to be contraindicated in patients with hepatic impairment.  Gemfibrozil is primarily metabolized by the liver.  Decreased drug metabolism may lead to accumulation and increased risk of toxicity, including biochemical abnormalities of liver function and musculoskeletal adverse effects.  Therapy with gemfibrozil should be administered cautiously in patients with active liver disease.  Liver function tests, including serum transaminase levels, should be performed periodically.  Therapy should be withdrawn if persistent abnormalities occur and are thought to be related to the drug.', '3', '', 'DDInter', 0);
