INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (53901, 25551, 'Gemifloxacin', 'Central Nervous System Diseases', 'Quinolones may cause CNS stimulation manifested as tremors, agitation, restlessness, anxiety, confusion, hallucinations, paranoia, insomnia, toxic psychosis, and/or seizures.  Benign intracranial hypertension has also been reported.  Therapy with quinolones should be administered cautiously in patients with or predisposed to seizures or other CNS abnormalities.  In addition, these patients should be advised to avoid the consumption of caffeine-containing products during therapy with some quinolones, most notably ciprofloxacin, enoxacin, and cinoxacin, since these agents can substantially reduce the clearance of caffeine and other methylxanthines, potentially resulting in severe CNS reactions.', '3', '', 'DDInter', 0);
