INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54501, 17530, 'Glimepiride', 'Hyponatremia', 'Treatment with sulfonylureas may cause hyponatremia, in patients who are on other medications or have medical conditions known to cause hyponatremia or increase release of antidiuretic hormone.  The syndrome of inappropriate antidiuretic hormone (SIADH) secretion has been reported with certain sulfonylureas and these drugs may augment the peripheral (antidiuretic) action of ADH and/or increase release of ADH.  Caution should be used when treating patients with hyponatremia or at greater risk of developing hyponatremia such as elderly patients, patients taking diuretics or those who are volume-depleted.', '2', '', 'DDInter', 0);
