INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54898, 0, 'Guanabenz', 'Bradycardia', 'Central alpha-2 adrenoreceptor agonists reduce sympathetic outflow from the central nervous system.  Heart rate is decreased, which may lead to or exacerbate sinus bradycardia and atrioventricular block.  Therapy with central alpha-2 adrenoreceptor agonists should be administered cautiously in patients with conduction disturbances such as sinus node dysfunction or AV nodal disease.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54899, 0, 'Guanabenz', 'Depressive Disorder', 'Central alpha-2 adrenoreceptor agonists may occasionally cause mental depression and should be used cautiously in patients with a history of depression.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54900, 0, 'Guanabenz', 'Hypotension', 'Central alpha-2 adrenoreceptor agonists reduce sympathetic outflow from the central nervous system, resulting in decreases in heart rate, peripheral and renovascular resistance, and blood pressure.  Therapy with these agents should be administered cautiously in patients with hypotension or conditions that may be exacerbated by decreased blood pressure and perfusion, such as coronary insufficiency, peripheral vascular disease (e.g., Raynaud''s syndrome), cerebrovascular disease, or recent myocardial infarction.', '2', '', 'DDInter', 0);
