INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54955, 0, 'Hemin', 'Blood Coagulation Disorders, Inherited', 'Hemin for injection has exhibited transient, mild anticoagulant effects during clinical studies, therefore, concurrent anticoagulant therapy should be avoided.  The extent and duration of the hypocoagulable state induced by hemin has not been established.  Caution should be exercised when using hemin therapy in patients with coagulation defects or risk of bleeding.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54956, 5905, 'Haloperidol', 'Dementia', 'Older patients with dementia-related psychosis treated with antipsychotic drugs are at an increased risk of death; although the causes were varied, most of the deaths appeared to be either cardiovascular (e.g., heart failure, sudden death) or infectious (e.g., pneumonia) in nature.  A causal relationship with antipsychotic use has not been established.  In controlled trials in older patients with dementia-related psychosis, patients randomized to risperidone, aripiprazole, and olanzapine had higher incidence of cerebrovascular adverse events (e.g., stroke, transient ischemic attack), including fatalities, compared to patients treated with placebo.  These agents are not approved for the treatment of patients with dementia-related psychosis.', '3', '', 'DDInter', 0);
