INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55076, 5905, 'Haloperidol', 'Liver Diseases', 'Haloperidol appears to be primarily converted in the liver to several metabolites, one of which is believed to be pharmacologically active.  The metabolites and approximately 1% of the parent drug are excreted in the urine.  Patients with impaired renal and/or hepatic function may be at greater risk for adverse effects due to drug and metabolite accumulation.  Therapy with haloperidol should be administered cautiously in such patients.  Lower initial dosages and slower titration may be appropriate.', '2', '', 'DDInter', 0);
