INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55086, 5905, 'Haloperidol', 'Hyperprolactinemia', 'Antipsychotic drugs can elevate serum prolactin concentrations, and this elevation persists during chronic administration.  This should be considered if therapy will be prescribed in patients with previously detected breast cancer as one-third of human breast cancers are prolactin-dependent in vitro.  Associated disturbances such as galactorrhea, amenorrhea, gynecomastia, and impotence have been reported.  Appropriate laboratory testing and follow-up is advised.', '2', '', 'DDInter', 0);
