INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55127, 0, 'Hepatitis A Vaccine', 'IMMUNE SUPPRESSION', 'The use of hepatitis A vaccine in immunocompromised persons, including individuals receiving immunosuppressive therapy, may result in diminished immune response to the vaccine and these patients may not be protected against hepatitis A virus.  Monitoring is recommended when vaccinating these patients.', '2', '', 'DDInter', 0);
