INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55155, 12915, 'Goserelin', 'Long QT Syndrome', 'Androgen deprivation therapy may prolong the QT/QTc interval.  Health care providers should consider whether the benefits of androgen deprivation therapy outweigh the potential risks in patients with congenital long QT syndrome, congestive heart failure, frequent electrolyte abnormalities, and in patients taking drugs known to prolong the QT interval.  Electrolyte abnormalities should be corrected.  Consider periodic monitoring of electrocardiograms and electrolytes.', '2', '', 'DDInter', 0);
