INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55573, 21197, 'Hydromorphone', 'Urinary Retention', 'Narcotic (opioid) analgesic agents may inhibit the urinary voiding reflex and increase the tone of the vesical sphincter in the bladder.  Acute urinary retention requiring catheterization may occur, particularly in patients with prostatic hypertrophy or urethral stricture and in elderly patients.  These agents may also decrease urine production via direct effects on the kidney and central stimulation of the release of vasopressin.  Therapy with opioids should be administered cautiously in patients with or predisposed to urinary retention and/or oliguria.  The effects on smooth muscle tone appear to be the most pronounced with morphine.', '2', '', 'DDInter', 0);
