INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55590, 880, 'Hydralazine', 'Bone Marrow Failure Disorders', 'Hydralazine may rarely cause blood dyscrasias at recommended dosages.  Reduction in hemoglobin, red cell count, agranulocytosis, leukopenia, and purpura have been reported.  Therapy with hydralazine should be administered cautiously in patients with these preexisting conditions and if such abnormalities develop during the course of therapy, treatment should be discontinued.  Monitoring blood counts should be considered for those patients at higher risk.', '3', '', 'DDInter', 0);
