INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55600, 880, 'Hydralazine', 'Lupus Erythematosus, Systemic', 'The use of hydralazine has been associated with the development of lupus erythematosus and lupus-like syndromes, as well as exacerbation of the disease.  Hydralazine therapy should be withdrawn in patients experiencing worsening of preexisting lupus.  Monitoring complete blood counts, and antinuclear antibody titers before and during prolonged therapy is recommended.', '3', '', 'DDInter', 0);
