INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55620, 24893, 'Amphotericin B (liposomal)', 'Water-Electrolyte Imbalance', 'All amphotericin B formulations may cause hypokalemia, hypomagnesemia, and less frequently, hypocalcemia.  Therapy with these agents should be administered cautiously in patients with preexisting electrolyte disturbances.  Serum electrolytes should be monitored closely.  Mineral and electrolyte supplementation may likely be required.  Complications of electrolyte imbalances include muscle weakness, tetany, seizures and cardiac disturbances.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55621, 6419, 'Human immunoglobulin G (intramuscular)', 'Thrombosis', 'Thrombosis has been reported after treatment with immune globulin products.  In general, patients at greatest risk of thromboembolic events are those of advanced age, prolonged immobilization, hypercoagulable conditions, history of venous or arterial thrombosis, use of estrogens, indwelling central vascular catheters, hyperviscosity, and cardiovascular risk factors.  Thrombosis may occur in the absence of known risk factors; therefore, it is recommended that patients treated with these agents should be monitored for signs and symptoms of thrombosis and assess blood viscosity in patients at risk.  Consider baseline assessment of blood viscosity in patients at risk for hyperviscosity.', '3', '', 'DDInter', 0);
