INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55664, 651, 'Ampicillin', 'Infectious Mononucleosis', 'Patients with mononucleosis treated with an aminopenicillin antibiotic, may develop a pruritic erythematous maculopapular skin rash.  The rash is usually self-limiting and resolves within days of discontinuing the offending agent.  An altered drug metabolism or an immune-mediated process unrelated to drug hypersensitivity has been proposed as the underlying mechanism.  Therapy with aminopenicillin antibiotics should not be administered in patients with mononucleosis.', '2', '', 'DDInter', 0);
