INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55798, 20271, 'Hydrochlorothiazide', 'Liver Diseases', 'Patients with severe liver disease or cirrhosis are very susceptible to thiazide-induced hypokalemic hypochloremic alkalosis.  Blood ammonia concentrations may be further increased in patients with previously elevated concentrations.  Hepatic encephalopathy and death have occurred secondary to the electrolyte alterations accompanying diuretic use.  Therapy with thiazide diuretics should be administered cautiously in patients with impaired hepatic function or progressive liver disease, and discontinued promptly if signs of impending hepatic coma appear (e.g., tremors, confusion, and increased jaundice).', '3', '', 'DDInter', 0);
