INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55801, 20271, 'Hydrochlorothiazide', 'Lupus Erythematosus, Systemic', 'The use of thiazide diuretics has been reported to possibly exacerbate or activate systemic lupus erythematosus.  Reported cases have generally been associated with chlorothiazide and hydrochlorothiazide.  Therapy with thiazide diuretics should be administered cautiously in patients with a history or risk of SLE.', '3', '', 'DDInter', 0);
