INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55813, 20271, 'Hydrochlorothiazide', 'Hyperlipidemias', 'Thiazide diuretics may increase serum triglyceride and cholesterol levels, primarily LDL and VLDL.  Whether these effects are dose-related and sustained during chronic therapy are unknown.  Patients with preexisting hyperlipidemia may require closer monitoring during thiazide therapy, and adjustments made accordingly in their lipid-lowering regimen', '2', '', 'DDInter', 0);
