INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55864, 6276, 'Hydroxyurea', 'Kidney Diseases', 'Hydroxyurea is primarily eliminated by the kidney.  Approximately 80% of urea is excreted in the urine as unchanged drug or urea.  Patients with marked renal impairment (CrCl < 60 mL/min or ESRD) may be at increased risk of toxicity associated with administration of hydroxyurea.  Therapy with hydroxyurea should be administered cautiously to patients with compromised renal function.', '3', '', 'DDInter', 0);
