INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55896, 306, 'Hydroxychloroquine', 'Ototoxicity', 'The use of 4-aminoquinolines has been associated rarely with ototoxicity.  Nerve-type deafness, which is usually irreversible, has occurred during long-term, high-dose therapy.  Deafness may not be apparent until several weeks after 4-aminoquinoline therapy.  Tinnitus and reduced hearing have been reported in patients with preexisting auditory damage.  Therapy with 4-aminoquinolines should be administered cautiously in such patients.  If new auditory defects develop or if hearing loss becomes worse, therapy should be immediately discontinued and the patient closely monitored.', '2', '', 'DDInter', 0);
